/**
 * Добавляет форму обратной связи
 */
tinymce.PluginManager.add('callbackform', (editor, url) => {
    editor.ui.registry.addButton('callbackform', {
        text: 'Форма обратной связи',
        onAction: () => {
            let timestamp = new Date().getTime();
            editor.insertContent(`<p id="callbackform-${timestamp}">[ФОРМА ОБРАТНОЙ СВЯЗИ]</p><p>&nbsp;</p>`);
        }
    });
});
